package com.twitter.producer.config;import lombok.RequiredArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.social.twitter.api.impl.TwitterTemplate;/** * The type Twitter config. * @author fabricer */@Slf4j@Configuration@RequiredArgsConstructorpublic class TwitterConfig {    private final TwitterProperties twitterProperties;    /**     * Twitter template twitter template.     *     * @return the twitter template     */    @Bean    public TwitterTemplate twitterTemplate() {        log.info("Configuring Twitter...");        return new TwitterTemplate(                twitterProperties.getAppId(),                twitterProperties.getAppSecret(),                twitterProperties.getAccessToken(),                twitterProperties.getAccessTokenSecret()        );    }}