package com.twitter.producer.config;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.stereotype.Component;/** * Twitter Properties specific to Twitter Social properties. * * <p> * Properties are configured in the application.yml file. * </p> * @author fabrice */@Component@ConfigurationProperties(prefix = "spring.social.twitter", ignoreUnknownFields = false)public class TwitterProperties {    private String appId;    private String appSecret;    private String accessToken;    private String accessTokenSecret;    /**     * Gets app id.     *     * @return the app id     */    public String getAppId() {        return appId;    }    /**     * Sets app id.     *     * @param appId the app id     */    public void setAppId(String appId) {        this.appId = appId;    }    /**     * Gets app secret.     *     * @return the app secret     */    public String getAppSecret() {        return appSecret;    }    /**     * Sets app secret.     *     * @param appSecret the app secret     */    public void setAppSecret(String appSecret) {        this.appSecret = appSecret;    }    /**     * Gets access token.     *     * @return the access token     */    public String getAccessToken() {        return accessToken;    }    /**     * Sets access token.     *     * @param accessToken the access token     */    public void setAccessToken(String accessToken) {        this.accessToken = accessToken;    }    /**     * Gets access token secret.     *     * @return the access token secret     */    public String getAccessTokenSecret() {        return accessTokenSecret;    }    /**     * Sets access token secret.     *     * @param accessTokenSecret the access token secret     */    public void setAccessTokenSecret(String accessTokenSecret) {        this.accessTokenSecret = accessTokenSecret;    }}