package com.twitter.producer.service.utils;import java.util.HashSet;import java.util.Set;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * The type Twitter strings utils. * @author fabrice */public class TwitterStringsUtils {    private static final Pattern TWITTER_HASHTAG_PATTERN = Pattern.compile("#\\w+");    private TwitterStringsUtils(){}    /**     * Tweet to hash tags set.     *     * @param text the text     * @return the set     */    public static Set<String> tweetToHashTags(String text) {        Set<String> hashTags = new HashSet<>();        Matcher matcher = TWITTER_HASHTAG_PATTERN.matcher(text);        while (matcher.find()) {            String handle = matcher.group();            hashTags.add(handle.substring(1));        }        return hashTags;    }}